(self.webpackChunk=self.webpackChunk||[]).push([["348"],{1511:function(s,n,a){"use strict";a.r(n);var t=a("7059");let l=["innerHTML"];n.default={setup:()=>({html:""}),render:()=>((0,t.wg)(),(0,t.iD)("div",{class:"van-doc-markdown-body",innerHTML:'<h1>Picker \u9009\u62E9\u5668</h1>\n<div class="van-doc-card"><h3 id="jie-shao" tabindex="-1">\u4ECB\u7ECD</h3>\n<p>\u63D0\u4F9B\u591A\u4E2A\u9009\u9879\u96C6\u5408\u4F9B\u7528\u6237\u9009\u62E9\uFF0C\u652F\u6301\u5355\u5217\u9009\u62E9\u548C\u591A\u5217\u7EA7\u8054\uFF0C\u901A\u5E38\u4E0E <a href="#/popup" target="_blank">\u5F39\u51FA\u5C42</a> \u7EC4\u4EF6\u914D\u5408\u4F7F\u7528\u3002</p>\n</div><div class="van-doc-card"><h3 id="yin-ru" tabindex="-1">\u5F15\u5165</h3>\n<p>\u5728<code>app.json</code>\u6216<code>index.json</code>\u4E2D\u5F15\u5165\u7EC4\u4EF6\uFF0C\u8BE6\u7EC6\u4ECB\u7ECD\u89C1<a href="#/quickstart#yin-ru-zu-jian" target="_blank">\u5FEB\u901F\u4E0A\u624B</a>\u3002</p>\n<pre><code class="language-json"><span class="hljs-attr">&quot;usingComponents&quot;</span><span class="hljs-punctuation">:</span> <span class="hljs-punctuation">{</span>\n  <span class="hljs-attr">&quot;van-picker&quot;</span><span class="hljs-punctuation">:</span> <span class="hljs-string">&quot;@vant/weapp/picker/index&quot;</span>\n<span class="hljs-punctuation">}</span>\n</code></pre>\n</div><h2 id="dai-ma-yan-shi" tabindex="-1">\u4EE3\u7801\u6F14\u793A</h2>\n<div class="van-doc-card"><h3 id="ji-chu-yong-fa" tabindex="-1">\u57FA\u7840\u7528\u6CD5</h3>\n<pre><code class="language-html"><span class="hljs-tag">&lt;<span class="hljs-name">van-picker</span> <span class="hljs-attr">columns</span>=<span class="hljs-string">&quot;{{ columns }}&quot;</span> <span class="hljs-attr">bind:change</span>=<span class="hljs-string">&quot;onChange&quot;</span> /&gt;</span>\n</code></pre>\n<pre><code class="language-javascript"><span class="hljs-keyword">import</span> <span class="hljs-title class_">Toast</span> <span class="hljs-keyword">from</span> <span class="hljs-string">&#x27;@vant/weapp/toast/toast&#x27;</span>;\n\n<span class="hljs-title class_">Page</span>({\n  <span class="hljs-attr">data</span>: {\n    <span class="hljs-attr">columns</span>: [<span class="hljs-string">&#x27;\u676D\u5DDE&#x27;</span>, <span class="hljs-string">&#x27;\u5B81\u6CE2&#x27;</span>, <span class="hljs-string">&#x27;\u6E29\u5DDE&#x27;</span>, <span class="hljs-string">&#x27;\u5609\u5174&#x27;</span>, <span class="hljs-string">&#x27;\u6E56\u5DDE&#x27;</span>],\n  },\n\n  <span class="hljs-title function_">onChange</span>(<span class="hljs-params">event</span>) {\n    <span class="hljs-keyword">const</span> { picker, value, index } = event.<span class="hljs-property">detail</span>;\n    <span class="hljs-title class_">Toast</span>(<span class="hljs-string">`\u5F53\u524D\u503C\uFF1A<span class="hljs-subst">${value}</span>, \u5F53\u524D\u7D22\u5F15\uFF1A<span class="hljs-subst">${index}</span>`</span>);\n  },\n});\n</code></pre>\n</div><div class="van-doc-card"><h3 id="mo-ren-xuan-zhong-xiang" tabindex="-1">\u9ED8\u8BA4\u9009\u4E2D\u9879</h3>\n<p>\u5355\u5217\u9009\u62E9\u5668\u53EF\u4EE5\u76F4\u63A5\u901A\u8FC7<code>default-index</code>\u5C5E\u6027\u8BBE\u7F6E\u521D\u59CB\u9009\u4E2D\u9879\u7684\u7D22\u5F15\u503C\u3002</p>\n<pre><code class="language-html"><span class="hljs-tag">&lt;<span class="hljs-name">van-picker</span>\n  <span class="hljs-attr">columns</span>=<span class="hljs-string">&quot;{{ columns }}&quot;</span>\n  <span class="hljs-attr">default-index</span>=<span class="hljs-string">&quot;{{ 2 }}&quot;</span>\n  <span class="hljs-attr">bind:change</span>=<span class="hljs-string">&quot;onChange&quot;</span>\n/&gt;</span>\n</code></pre>\n</div><div class="van-doc-card"><h3 id="zhan-shi-ding-bu-lan" tabindex="-1">\u5C55\u793A\u9876\u90E8\u680F</h3>\n<pre><code class="language-html"><span class="hljs-tag">&lt;<span class="hljs-name">van-picker</span>\n  <span class="hljs-attr">show-toolbar</span>\n  <span class="hljs-attr">title</span>=<span class="hljs-string">&quot;\u6807\u9898&quot;</span>\n  <span class="hljs-attr">columns</span>=<span class="hljs-string">&quot;{{ columns }}&quot;</span>\n  <span class="hljs-attr">bind:cancel</span>=<span class="hljs-string">&quot;onCancel&quot;</span>\n  <span class="hljs-attr">bind:confirm</span>=<span class="hljs-string">&quot;onConfirm&quot;</span>\n/&gt;</span>\n</code></pre>\n<pre><code class="language-javascript"><span class="hljs-keyword">import</span> <span class="hljs-title class_">Toast</span> <span class="hljs-keyword">from</span> <span class="hljs-string">&#x27;@vant/weapp/toast/toast&#x27;</span>;\n\n<span class="hljs-title class_">Page</span>({\n  <span class="hljs-attr">data</span>: {\n    <span class="hljs-attr">columns</span>: [<span class="hljs-string">&#x27;\u676D\u5DDE&#x27;</span>, <span class="hljs-string">&#x27;\u5B81\u6CE2&#x27;</span>, <span class="hljs-string">&#x27;\u6E29\u5DDE&#x27;</span>, <span class="hljs-string">&#x27;\u5609\u5174&#x27;</span>, <span class="hljs-string">&#x27;\u6E56\u5DDE&#x27;</span>],\n  },\n\n  <span class="hljs-title function_">onConfirm</span>(<span class="hljs-params">event</span>) {\n    <span class="hljs-keyword">const</span> { picker, value, index } = event.<span class="hljs-property">detail</span>;\n    <span class="hljs-title class_">Toast</span>(<span class="hljs-string">`\u5F53\u524D\u503C\uFF1A<span class="hljs-subst">${value}</span>, \u5F53\u524D\u7D22\u5F15\uFF1A<span class="hljs-subst">${index}</span>`</span>);\n  },\n\n  <span class="hljs-title function_">onCancel</span>(<span class="hljs-params"></span>) {\n    <span class="hljs-title class_">Toast</span>(<span class="hljs-string">&#x27;\u53D6\u6D88&#x27;</span>);\n  },\n});\n</code></pre>\n</div><div class="van-doc-card"><h3 id="duo-lie-lian-dong" tabindex="-1">\u591A\u5217\u8054\u52A8</h3>\n<pre><code class="language-html"><span class="hljs-tag">&lt;<span class="hljs-name">van-picker</span> <span class="hljs-attr">columns</span>=<span class="hljs-string">&quot;{{ columns }}&quot;</span> <span class="hljs-attr">bind:change</span>=<span class="hljs-string">&quot;onChange&quot;</span> /&gt;</span>\n</code></pre>\n<pre><code class="language-javascript"><span class="hljs-keyword">const</span> citys = {\n  \u6D59\u6C5F: [<span class="hljs-string">&#x27;\u676D\u5DDE&#x27;</span>, <span class="hljs-string">&#x27;\u5B81\u6CE2&#x27;</span>, <span class="hljs-string">&#x27;\u6E29\u5DDE&#x27;</span>, <span class="hljs-string">&#x27;\u5609\u5174&#x27;</span>, <span class="hljs-string">&#x27;\u6E56\u5DDE&#x27;</span>],\n  \u798F\u5EFA: [<span class="hljs-string">&#x27;\u798F\u5DDE&#x27;</span>, <span class="hljs-string">&#x27;\u53A6\u95E8&#x27;</span>, <span class="hljs-string">&#x27;\u8386\u7530&#x27;</span>, <span class="hljs-string">&#x27;\u4E09\u660E&#x27;</span>, <span class="hljs-string">&#x27;\u6CC9\u5DDE&#x27;</span>],\n};\n\n<span class="hljs-title class_">Page</span>({\n  <span class="hljs-attr">data</span>: {\n    <span class="hljs-attr">columns</span>: [\n      {\n        <span class="hljs-attr">values</span>: <span class="hljs-title class_">Object</span>.<span class="hljs-title function_">keys</span>(citys),\n        <span class="hljs-attr">className</span>: <span class="hljs-string">&#x27;column1&#x27;</span>,\n      },\n      {\n        <span class="hljs-attr">values</span>: citys[<span class="hljs-string">&#x27;\u6D59\u6C5F&#x27;</span>],\n        <span class="hljs-attr">className</span>: <span class="hljs-string">&#x27;column2&#x27;</span>,\n        <span class="hljs-attr">defaultIndex</span>: <span class="hljs-number">2</span>,\n      },\n    ],\n  },\n\n  <span class="hljs-title function_">onChange</span>(<span class="hljs-params">event</span>) {\n    <span class="hljs-keyword">const</span> { picker, value, index } = event.<span class="hljs-property">detail</span>;\n    picker.<span class="hljs-title function_">setColumnValues</span>(<span class="hljs-number">1</span>, citys[value[<span class="hljs-number">0</span>]]);\n  },\n});\n</code></pre>\n</div><div class="van-doc-card"><h3 id="jin-yong-xuan-xiang" tabindex="-1">\u7981\u7528\u9009\u9879</h3>\n<p>\u9009\u9879\u53EF\u4EE5\u4E3A\u5BF9\u8C61\u7ED3\u6784\uFF0C\u901A\u8FC7\u8BBE\u7F6E disabled \u6765\u7981\u7528\u8BE5\u9009\u9879\u3002</p>\n<pre><code class="language-html"><span class="hljs-tag">&lt;<span class="hljs-name">van-picker</span> <span class="hljs-attr">columns</span>=<span class="hljs-string">&quot;{{ columns }}&quot;</span> /&gt;</span>\n</code></pre>\n<pre><code class="language-javascript"><span class="hljs-title class_">Page</span>({\n  <span class="hljs-attr">data</span>: {\n    <span class="hljs-attr">columns</span>: [\n      { <span class="hljs-attr">text</span>: <span class="hljs-string">&#x27;\u676D\u5DDE&#x27;</span>, <span class="hljs-attr">disabled</span>: <span class="hljs-literal">true</span> },\n      { <span class="hljs-attr">text</span>: <span class="hljs-string">&#x27;\u5B81\u6CE2&#x27;</span> },\n      { <span class="hljs-attr">text</span>: <span class="hljs-string">&#x27;\u6E29\u5DDE&#x27;</span> },\n    ],\n  },\n});\n</code></pre>\n</div><div class="van-doc-card"><h3 id="jia-zai-zhuang-tai" tabindex="-1">\u52A0\u8F7D\u72B6\u6001</h3>\n<p>\u5F53 Picker \u6570\u636E\u662F\u901A\u8FC7\u5F02\u6B65\u83B7\u53D6\u65F6\uFF0C\u53EF\u4EE5\u901A\u8FC7 <code>loading</code> \u5C5E\u6027\u663E\u793A\u52A0\u8F7D\u63D0\u793A\u3002</p>\n<pre><code class="language-html"><span class="hljs-tag">&lt;<span class="hljs-name">van-picker</span> <span class="hljs-attr">columns</span>=<span class="hljs-string">&quot;{{ columns }}&quot;</span> <span class="hljs-attr">loading</span> /&gt;</span>\n</code></pre>\n</div><h2 id="api" tabindex="-1">API</h2>\n<div class="van-doc-card"><h3 id="props" tabindex="-1">Props</h3>\n<table>\n<thead>\n<tr>\n<th>\u53C2\u6570</th>\n<th>\u8BF4\u660E</th>\n<th>\u7C7B\u578B</th>\n<th>\u9ED8\u8BA4\u503C</th>\n</tr>\n</thead>\n<tbody>\n<tr>\n<td>columns</td>\n<td>\u5BF9\u8C61\u6570\u7EC4\uFF0C\u914D\u7F6E\u6BCF\u4E00\u5217\u663E\u793A\u7684\u6570\u636E</td>\n<td><em>Array</em></td>\n<td><code>[]</code></td>\n</tr>\n<tr>\n<td>show-toolbar</td>\n<td>\u662F\u5426\u663E\u793A\u9876\u90E8\u680F</td>\n<td><em>boolean</em></td>\n<td><code>false</code></td>\n</tr>\n<tr>\n<td>toolbar-position</td>\n<td>\u9876\u90E8\u680F\u4F4D\u7F6E\uFF0C\u53EF\u9009\u503C\u4E3A<code>bottom</code></td>\n<td><em>string</em></td>\n<td><code>top</code></td>\n</tr>\n<tr>\n<td>title</td>\n<td>\u9876\u90E8\u680F\u6807\u9898</td>\n<td><em>string</em></td>\n<td><code>\'\'</code></td>\n</tr>\n<tr>\n<td>loading</td>\n<td>\u662F\u5426\u663E\u793A\u52A0\u8F7D\u72B6\u6001</td>\n<td><em>boolean</em></td>\n<td><code>false</code></td>\n</tr>\n<tr>\n<td>value-key</td>\n<td>\u9009\u9879\u5BF9\u8C61\u4E2D\uFF0C\u6587\u5B57\u5BF9\u5E94\u7684 key</td>\n<td><em>string</em></td>\n<td><code>text</code></td>\n</tr>\n<tr>\n<td>item-height</td>\n<td>\u9009\u9879\u9AD8\u5EA6</td>\n<td><em>number</em></td>\n<td><code>44</code></td>\n</tr>\n<tr>\n<td>confirm-button-text</td>\n<td>\u786E\u8BA4\u6309\u94AE\u6587\u5B57</td>\n<td><em>string</em></td>\n<td><code>\u786E\u8BA4</code></td>\n</tr>\n<tr>\n<td>cancel-button-text</td>\n<td>\u53D6\u6D88\u6309\u94AE\u6587\u5B57</td>\n<td><em>string</em></td>\n<td><code>\u53D6\u6D88</code></td>\n</tr>\n<tr>\n<td>visible-item-count</td>\n<td>\u53EF\u89C1\u7684\u9009\u9879\u4E2A\u6570</td>\n<td><em>number</em></td>\n<td><code>6</code></td>\n</tr>\n<tr>\n<td>default-index</td>\n<td>\u5355\u5217\u9009\u62E9\u5668\u7684\u9ED8\u8BA4\u9009\u4E2D\u9879\u7D22\u5F15\uFF0C<br>\u591A\u5217\u9009\u62E9\u5668\u8BF7\u53C2\u8003\u4E0B\u65B9\u7684 Columns \u914D\u7F6E</td>\n<td><em>number</em></td>\n<td><code>0</code></td>\n</tr>\n</tbody>\n</table>\n</div><div class="van-doc-card"><h3 id="events" tabindex="-1">Events</h3>\n<p>Picker \u7EC4\u4EF6\u7684\u4E8B\u4EF6\u4F1A\u6839\u636E columns \u662F\u5355\u5217\u6216\u591A\u5217\u8FD4\u56DE\u4E0D\u540C\u7684\u53C2\u6570\u3002</p>\n<table>\n<thead>\n<tr>\n<th>\u4E8B\u4EF6\u540D</th>\n<th>\u8BF4\u660E</th>\n<th>\u53C2\u6570</th>\n</tr>\n</thead>\n<tbody>\n<tr>\n<td>bind:confirm</td>\n<td>\u70B9\u51FB\u5B8C\u6210\u6309\u94AE\u65F6\u89E6\u53D1</td>\n<td>\u5355\u5217\uFF1A\u9009\u4E2D\u503C\uFF0C\u9009\u4E2D\u503C\u5BF9\u5E94\u7684\u7D22\u5F15<br>\u591A\u5217\uFF1A\u6240\u6709\u5217\u9009\u4E2D\u503C\uFF0C\u6240\u6709\u5217\u9009\u4E2D\u503C\u5BF9\u5E94\u7684\u7D22\u5F15</td>\n</tr>\n<tr>\n<td>bind:cancel</td>\n<td>\u70B9\u51FB\u53D6\u6D88\u6309\u94AE\u65F6\u89E6\u53D1</td>\n<td>\u5355\u5217\uFF1A\u9009\u4E2D\u503C\uFF0C\u9009\u4E2D\u503C\u5BF9\u5E94\u7684\u7D22\u5F15<br>\u591A\u5217\uFF1A\u6240\u6709\u5217\u9009\u4E2D\u503C\uFF0C\u6240\u6709\u5217\u9009\u4E2D\u503C\u5BF9\u5E94\u7684\u7D22\u5F15</td>\n</tr>\n<tr>\n<td>bind:change</td>\n<td>\u9009\u9879\u6539\u53D8\u65F6\u89E6\u53D1</td>\n<td>\u5355\u5217\uFF1APicker \u5B9E\u4F8B\uFF0C\u9009\u4E2D\u503C\uFF0C\u9009\u4E2D\u503C\u5BF9\u5E94\u7684\u7D22\u5F15<br>\u591A\u5217\uFF1APicker \u5B9E\u4F8B\uFF0C\u6240\u6709\u5217\u9009\u4E2D\u503C\uFF0C\u5F53\u524D\u5217\u5BF9\u5E94\u7684\u7D22\u5F15</td>\n</tr>\n</tbody>\n</table>\n</div><div class="van-doc-card"><h3 id="columns-shu-ju-jie-gou" tabindex="-1">Columns \u6570\u636E\u7ED3\u6784</h3>\n<p>\u5F53\u4F20\u5165\u591A\u5217\u6570\u636E\u65F6\uFF0C<code>columns</code>\u4E3A\u4E00\u4E2A\u5BF9\u8C61\u6570\u7EC4\uFF0C\u6570\u7EC4\u4E2D\u7684\u6BCF\u4E00\u4E2A\u5BF9\u8C61\u914D\u7F6E\u6BCF\u4E00\u5217\uFF0C\u6BCF\u4E00\u5217\u6709\u4EE5\u4E0B<code>key</code>\u3002</p>\n<table>\n<thead>\n<tr>\n<th>key</th>\n<th>\u8BF4\u660E</th>\n</tr>\n</thead>\n<tbody>\n<tr>\n<td>values</td>\n<td>\u5217\u4E2D\u5BF9\u5E94\u7684\u5907\u9009\u503C</td>\n</tr>\n<tr>\n<td>defaultIndex</td>\n<td>\u521D\u59CB\u9009\u4E2D\u9879\u7684\u7D22\u5F15\uFF0C\u9ED8\u8BA4\u4E3A 0</td>\n</tr>\n</tbody>\n</table>\n</div><div class="van-doc-card"><h3 id="wai-bu-yang-shi-lei" tabindex="-1">\u5916\u90E8\u6837\u5F0F\u7C7B</h3>\n<table>\n<thead>\n<tr>\n<th>\u7C7B\u540D</th>\n<th>\u8BF4\u660E</th>\n</tr>\n</thead>\n<tbody>\n<tr>\n<td>custom-class</td>\n<td>\u6839\u8282\u70B9\u6837\u5F0F\u7C7B</td>\n</tr>\n<tr>\n<td>active-class</td>\n<td>\u9009\u4E2D\u9879\u6837\u5F0F\u7C7B</td>\n</tr>\n<tr>\n<td>toolbar-class</td>\n<td>\u9876\u90E8\u680F\u6837\u5F0F\u7C7B</td>\n</tr>\n<tr>\n<td>column-class</td>\n<td>\u5217\u6837\u5F0F\u7C7B</td>\n</tr>\n</tbody>\n</table>\n</div><div class="van-doc-card"><h3 id="fang-fa" tabindex="-1">\u65B9\u6CD5</h3>\n<p>\u901A\u8FC7 selectComponent \u53EF\u4EE5\u83B7\u53D6\u5230 picker \u5B9E\u4F8B\u5E76\u8C03\u7528\u5B9E\u4F8B\u65B9\u6CD5\u3002</p>\n<table>\n<thead>\n<tr>\n<th>\u65B9\u6CD5\u540D</th>\n<th>\u53C2\u6570</th>\n<th>\u8FD4\u56DE\u503C</th>\n<th>\u4ECB\u7ECD</th>\n</tr>\n</thead>\n<tbody>\n<tr>\n<td>getValues</td>\n<td>-</td>\n<td>values</td>\n<td>\u83B7\u53D6\u6240\u6709\u5217\u9009\u4E2D\u7684\u503C</td>\n</tr>\n<tr>\n<td>setValues</td>\n<td>values</td>\n<td>-</td>\n<td>\u8BBE\u7F6E\u6240\u6709\u5217\u9009\u4E2D\u7684\u503C</td>\n</tr>\n<tr>\n<td>getIndexes</td>\n<td>-</td>\n<td>indexes</td>\n<td>\u83B7\u53D6\u6240\u6709\u5217\u9009\u4E2D\u503C\u5BF9\u5E94\u7684\u7D22\u5F15</td>\n</tr>\n<tr>\n<td>setIndexes</td>\n<td>indexes</td>\n<td>-</td>\n<td>\u8BBE\u7F6E\u6240\u6709\u5217\u9009\u4E2D\u503C\u5BF9\u5E94\u7684\u7D22\u5F15</td>\n</tr>\n<tr>\n<td>getColumnValue</td>\n<td>columnIndex</td>\n<td>value</td>\n<td>\u83B7\u53D6\u5BF9\u5E94\u5217\u9009\u4E2D\u7684\u503C</td>\n</tr>\n<tr>\n<td>setColumnValue</td>\n<td>columnIndex, value</td>\n<td>-</td>\n<td>\u8BBE\u7F6E\u5BF9\u5E94\u5217\u9009\u4E2D\u7684\u503C</td>\n</tr>\n<tr>\n<td>getColumnIndex</td>\n<td>columnIndex</td>\n<td>optionIndex</td>\n<td>\u83B7\u53D6\u5BF9\u5E94\u5217\u9009\u4E2D\u9879\u7684\u7D22\u5F15</td>\n</tr>\n<tr>\n<td>setColumnIndex</td>\n<td>columnIndex, optionIndex</td>\n<td>-</td>\n<td>\u8BBE\u7F6E\u5BF9\u5E94\u5217\u9009\u4E2D\u9879\u7684\u7D22\u5F15</td>\n</tr>\n<tr>\n<td>getColumnValues</td>\n<td>columnIndex</td>\n<td>values</td>\n<td>\u83B7\u53D6\u5BF9\u5E94\u5217\u4E2D\u6240\u6709\u9009\u9879</td>\n</tr>\n<tr>\n<td>setColumnValues</td>\n<td>columnIndex, values</td>\n<td>-</td>\n<td>\u8BBE\u7F6E\u5BF9\u5E94\u5217\u4E2D\u6240\u6709\u9009\u9879</td>\n</tr>\n</tbody>\n</table>\n</div>'},null,8,l))}}}]);